allContent.content:
---
layout: base.njk
pagination:
  data: collections.authors
  size: 1
  alias: author
permalink: /authors/{{ author.data.slug }}/
eleventyExcludeFromCollections: true
eleventyComputed:
  title: "{{ author.data.name }} - Author at EvolvedLotus"
  description: "{{ author.data.bio }}"
---

<div class="author-profile-page">
  <div class="author-hero">
    <div class="author-hero-content">
      {% if author.data.avatar %}
      <img src="{{ author.data.avatar }}" alt="{{ author.data.name }}" class="author-avatar-large">
      {% else %}
      <div class="author-avatar-placeholder">
        {{ author.data.name | slice(0, 2) | upper }}
      </div>
      {% endif %}
      
      <div class="author-info">
        <h1 class="author-name">{{ author.data.name }}</h1>
        {% if author.data.role %}
        <p class="author-role">{{ author.data.role }}</p>
        {% endif %}
        
        {% if author.data.social %}
        <div class="author-social-links">
          {% if author.data.social.twitter %}
          <a href="{{ author.data.social.twitter }}" target="_blank" rel="noopener" class="social-link" aria-label="Twitter">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
              <path d="M18.244 2.25h3.308l-7.227 8.26 8.502 11.24H16.17l-5.214-6.817L4.99 21.75H1.68l7.73-8.835L1.254 2.25H8.08l4.713 6.231zm-1.161 17.52h1.833L7.084 4.126H5.117z"/>
            </svg>
          </a>
          {% endif %}
          
          {% if author.data.social.instagram %}
          <a href="{{ author.data.social.instagram }}" target="_blank" rel="noopener" class="social-link" aria-label="Instagram">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
              <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
            </svg>
          </a>
          {% endif %}
          
          {% if author.data.social.tiktok %}
          <a href="{{ author.data.social.tiktok }}" target="_blank" rel="noopener" class="social-link" aria-label="TikTok">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
              <path d="M19.59 6.69a4.83 4.83 0 0 1-3.77-4.25V2h-3.45v13.67a2.89 2.89 0 0 1-5.2 1.74 2.89 2.89 0 0 1 2.31-4.64 2.93 2.93 0 0 1 .88.13V9.4a6.84 6.84 0 0 0-1-.05A6.33 6.33 0 0 0 5 20.1a6.34 6.34 0 0 0 10.86-4.43v-7a8.16 8.16 0 0 0 4.77 1.52v-3.4a4.85 4.85 0 0 1-1-.1z"/>
            </svg>
          </a>
          {% endif %}
          
          {% if author.data.social.youtube %}
          <a href="{{ author.data.social.youtube }}" target="_blank" rel="noopener" class="social-link" aria-label="YouTube">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
              <path d="M23.498 6.186a3.016 3.016 0 0 0-2.122-2.136C19.505 3.545 12 3.545 12 3.545s-7.505 0-9.377.505A3.017 3.017 0 0 0 .502 6.186C0 8.07 0 12 0 12s0 3.93.502 5.814a3.016 3.016 0 0 0 2.122 2.136c1.871.505 9.376.505 9.376.505s7.505 0 9.377-.505a3.015 3.015 0 0 0 2.122-2.136C24 15.93 24 12 24 12s0-3.93-.502-5.814zM9.545 15.568V8.432L15.818 12l-6.273 3.568z"/>
            </svg>
          </a>
          {% endif %}
        </div>
        {% endif %}
      </div>
    </div>
  </div>

  <div class="author-content">
    <div class="author-bio-section">
      <h2>About {{ author.data.name }}</h2>
      <p class="author-bio">{{ author.data.bio }}</p>
      
      {% if author.data.expertise %}
      <div class="author-expertise">
        <h3>Expertise</h3>
        <ul class="expertise-list">
          {% for skill in author.data.expertise %}
          <li class="expertise-item">{{ skill }}</li>
          {% endfor %}
        </ul>
      </div>
      {% endif %}
    </div>

    <div class="author-posts-section">
      <h2>Articles by {{ author.data.name }}</h2>
      
      {% set authorPosts = collections.blog | filterByAuthor(author.data.name) %}
      
      {% if authorPosts.length > 0 %}
      <div class="author-posts-grid">
        {% for post in authorPosts %}
        <article class="author-post-card">
          {% if post.data.image %}
          <a href="{{ post.url }}" class="post-card-image">
            <img src="{{ post.data.image }}" alt="{{ post.data.imageAlt | default(post.data.title) }}" loading="lazy">
          </a>
          {% endif %}
          
          <div class="post-card-content">
            <div class="post-card-meta">
              <time datetime="{{ post.data.date | isoDate }}">{{ post.data.date | postDate }}</time>
              {% if post.data.hub %}
              <span class="post-card-hub">{{ post.data.hub }}</span>
              {% endif %}
            </div>
            
            <h3 class="post-card-title">
              <a href="{{ post.url }}">{{ post.data.title }}</a>
            </h3>
            
            {% if post.data.description %}
            <p class="post-card-description">{{ post.data.description }}</p>
            {% endif %}
            
            <a href="{{ post.url }}" class="post-card-link">Read More →</a>
          </div>
        </article>
        {% endfor %}
      </div>
      {% else %}
      <p class="no-posts">No articles yet. Check back soon!</p>
      {% endif %}
    </div>
  </div>
</div>

<style>
.author-profile-page {
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem 1rem;
}

.author-hero {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 16px;
  padding: 3rem 2rem;
  margin-bottom: 3rem;
  color: white;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
}

.author-hero-content {
  display: flex;
  align-items: center;
  gap: 2rem;
}

.author-avatar-large {
  width: 150px;
  height: 150px;
  border-radius: 50%;
  border: 4px solid white;
  object-fit: cover;
  flex-shrink: 0;
}

.author-avatar-placeholder {
  width: 150px;
  height: 150px;
  border-radius: 50%;
  border: 4px solid white;
  background: rgba(255, 255, 255, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
  font-weight: 700;
  flex-shrink: 0;
}

.author-info {
  flex: 1;
}

.author-name {
  font-size: 2.5rem;
  font-weight: 700;
  margin: 0 0 0.5rem 0;
}

.author-role {
  font-size: 1.25rem;
  opacity: 0.9;
  margin: 0 0 1rem 0;
}

.author-social-links {
  display: flex;
  gap: 1rem;
}

.social-link {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  text-decoration: none;
  transition: all 0.2s ease;
}

.social-link:hover {
  background: white;
  color: #667eea;
  transform: translateY(-2px);
}

.author-content {
  display: grid;
  gap: 3rem;
}

.author-bio-section h2,
.author-posts-section h2 {
  font-size: 1.75rem;
  font-weight: 700;
  margin: 0 0 1.5rem 0;
  color: #111827;
}

.author-bio {
  font-size: 1.125rem;
  line-height: 1.8;
  color: #374151;
  margin-bottom: 2rem;
}

.author-expertise h3 {
  font-size: 1.25rem;
  font-weight: 600;
  margin: 0 0 1rem 0;
  color: #111827;
}

.expertise-list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem;
}

.expertise-item {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 9999px;
  font-size: 0.875rem;
  font-weight: 600;
}

.author-posts-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
}

.author-post-card {
  background: white;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  transition: all 0.2s ease;
}

.author-post-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
}

.post-card-image {
  display: block;
  overflow: hidden;
}

.post-card-image img {
  width: 100%;
  height: 200px;
  object-fit: cover;
  transition: transform 0.2s ease;
}

.author-post-card:hover .post-card-image img {
  transform: scale(1.05);
}

.post-card-content {
  padding: 1.5rem;
}

.post-card-meta {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 0.75rem;
  font-size: 0.875rem;
  color: #6b7280;
}

.post-card-hub {
  background: #e5e7eb;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-weight: 600;
  color: #374151;
}

.post-card-title {
  font-size: 1.25rem;
  font-weight: 700;
  margin: 0 0 0.75rem 0;
}

.post-card-title a {
  color: #111827;
  text-decoration: none;
  transition: color 0.2s ease;
}

.post-card-title a:hover {
  color: #667eea;
}

.post-card-description {
  color: #6b7280;
  line-height: 1.6;
  margin: 0 0 1rem 0;
}

.post-card-link {
  color: #667eea;
  font-weight: 600;
  text-decoration: none;
  transition: color 0.2s ease;
}

.post-card-link:hover {
  color: #764ba2;
}

.no-posts {
  text-align: center;
  padding: 3rem;
  color: #6b7280;
  font-size: 1.125rem;
}

@media (max-width: 768px) {
  .author-hero {
    padding: 2rem 1.5rem;
  }

  .author-hero-content {
    flex-direction: column;
    text-align: center;
  }

  .author-name {
    font-size: 2rem;
  }

  .author-social-links {
    justify-content: center;
  }

  .author-posts-grid {
    grid-template-columns: 1fr;
  }
}
</style>

---
