<script>
document.addEventListener('DOMContentLoaded', function() {
  const calculators = document.querySelectorAll('.interactive-calculator');
  
  calculators.forEach(calc => {
    const button = calc.querySelector('.calc-button');
    const result = calc.querySelector('.calc-result');
    const calcType = button.dataset.calcType;
    
    button.addEventListener('click', function() {
      if (calcType === 'engagement-rate') {
        calculateEngagementRate(calc, result);
      } else if (calcType === 'posting-time') {
        calculateBestPostingTime(calc, result);
      } else if (calcType === 'reach-estimate') {
        calculateReachEstimate(calc, result);
      }
    });
  });
  
  function calculateEngagementRate(calc, result) {
    const likes = parseInt(calc.querySelector('[id^="likes-"]').value) || 0;
    const comments = parseInt(calc.querySelector('[id^="comments-"]').value) || 0;
    const shares = parseInt(calc.querySelector('[id^="shares-"]').value) || 0;
    const followers = parseInt(calc.querySelector('[id^="followers-"]').value) || 1;
    
    const totalEngagement = likes + comments + shares;
    const engagementRate = ((totalEngagement / followers) * 100).toFixed(2);
    
    let interpretation = '';
    if (engagementRate >= 10) {
      interpretation = '🔥 Exceptional! Your content is highly engaging!';
    } else if (engagementRate >= 5) {
      interpretation = '⭐ Great! You have strong audience engagement!';
    } else if (engagementRate >= 2) {
      interpretation = '👍 Good! Your engagement is above average!';
    } else if (engagementRate >= 1) {
      interpretation = '📈 Fair. There\'s room for improvement!';
    } else {
      interpretation = '💡 Focus on creating more engaging content!';
    }
    
    result.querySelector('.result-value').textContent = engagementRate + '%';
    result.querySelector('.result-interpretation').textContent = interpretation;
    result.style.display = 'block';
    
    // Track calculation
    if (typeof gtag !== 'undefined') {
      gtag('event', 'calculator_use', {
        calculator_type: 'engagement_rate',
        result: engagementRate
      });
    }
  }
  
  function calculateBestPostingTime(calc, result) {
    const platform = calc.querySelector('[id^="platform-"]').value;
    const audience = calc.querySelector('[id^="audience-"]').value;
    const timezone = calc.querySelector('[id^="timezone-"]').value;
    
    // Best posting times database (simplified)
    const postingTimes = {
      tiktok: {
        general: ['6-9 AM', '12-1 PM', '7-9 PM'],
        teens: ['3-6 PM', '7-10 PM'],
        'young-adults': ['12-2 PM', '7-11 PM'],
        adults: ['6-9 AM', '12-1 PM', '5-7 PM'],
        professionals: ['7-9 AM', '12-1 PM', '5-6 PM']
      },
      instagram: {
        general: ['6-9 AM', '12-2 PM', '5-7 PM'],
        teens: ['3-5 PM', '7-9 PM'],
        'young-adults': ['11 AM-1 PM', '7-9 PM'],
        adults: ['6-8 AM', '12-2 PM', '5-7 PM'],
        professionals: ['7-9 AM', '12-1 PM', '5-6 PM']
      },
      youtube: {
        general: ['2-4 PM', '6-9 PM'],
        teens: ['3-6 PM', '7-10 PM'],
        'young-adults': ['12-3 PM', '7-10 PM'],
        adults: ['12-3 PM', '7-10 PM'],
        professionals: ['12-2 PM', '6-9 PM']
      },
      twitter: {
        general: ['8-10 AM', '12-1 PM', '5-6 PM'],
        teens: ['3-5 PM', '8-10 PM'],
        'young-adults': ['12-1 PM', '5-6 PM'],
        adults: ['8-10 AM', '12-1 PM', '5-6 PM'],
        professionals: ['7-9 AM', '12-1 PM', '5-6 PM']
      },
      facebook: {
        general: ['9 AM-12 PM', '1-3 PM'],
        teens: ['3-5 PM'],
        'young-adults': ['12-2 PM', '7-9 PM'],
        adults: ['9 AM-12 PM', '1-3 PM'],
        professionals: ['9 AM-12 PM', '1-2 PM']
      }
    };
    
    const times = postingTimes[platform][audience];
    
    const timesHTML = `
      <h4>Best Times to Post on ${platform.charAt(0).toUpperCase() + platform.slice(1)}</h4>
      <p>For ${audience.replace('-', ' ')} audience in ${timezone} timezone:</p>
      <ul>
        ${times.map(time => `<li>⏰ ${time}</li>`).join('')}
      </ul>
      <p style="margin-top: 1rem; font-size: 0.9rem; opacity: 0.9;">
        💡 Tip: Test these times and track your analytics to find what works best for YOUR specific audience!
      </p>
    `;
    
    result.querySelector('.result-times').innerHTML = timesHTML;
    result.style.display = 'block';
    
    // Track calculation
    if (typeof gtag !== 'undefined') {
      gtag('event', 'calculator_use', {
        calculator_type: 'posting_time',
        platform: platform,
        audience: audience
      });
    }
  }
  
  function calculateReachEstimate(calc, result) {
    const followers = parseInt(calc.querySelector('[id^="followers-reach-"]').value) || 0;
    const engagementRate = parseFloat(calc.querySelector('[id^="engagement-"]').value) || 0;
    const platform = calc.querySelector('[id^="platform-reach-"]').value;
    
    // Platform reach multipliers (simplified estimates)
    const reachMultipliers = {
      tiktok: 3.5,      // TikTok has high viral potential
      instagram: 1.2,   // Instagram reach is limited
      youtube: 2.0,     // YouTube has good recommendation system
      twitter: 1.5      // Twitter has moderate reach
    };
    
    const multiplier = reachMultipliers[platform];
    const organicReach = Math.round(followers * (engagementRate / 100) * multiplier);
    const minReach = Math.round(organicReach * 0.7);
    const maxReach = Math.round(organicReach * 1.5);
    
    const reachHTML = `
      <h4>Estimated Reach</h4>
      <div class="result-value">${organicReach.toLocaleString()}</div>
      <p>Expected range: ${minReach.toLocaleString()} - ${maxReach.toLocaleString()} people</p>
      <p style="margin-top: 1rem; font-size: 0.9rem; opacity: 0.9;">
        📊 This estimate is based on your follower count, engagement rate, and ${platform}'s typical reach patterns.
        Actual reach can vary significantly based on content quality, timing, and trends.
      </p>
    `;
    
    result.querySelector('.result-reach').innerHTML = reachHTML;
    result.style.display = 'block';
    
    // Track calculation
    if (typeof gtag !== 'undefined') {
      gtag('event', 'calculator_use', {
        calculator_type: 'reach_estimate',
        platform: platform,
        result: organicReach
      });
    }
  }
});
</script>
{% endmacro %}

{% macro quiz(quizData) %}
<div class="interactive-quiz" data-quiz-id="{{ quizData.id }}">
  <div class="quiz-header">
    <h3 class="quiz-title">{{ quizData.title }}</h3>
    {% if quizData.description %}
    <p class="quiz-description">{{ quizData.description }}</p>
    {% endif %}
  </div>

  <div class="quiz-questions">
    {% for question in quizData.questions %}
    <div class="quiz-question" data-question-index="{{ loop.index0 }}" data-correct-answer="{{ question.correctAnswer }}">
      <div class="question-header">
        <span class="question-number">Question {{ loop.index }} of {{ quizData.questions.length }}</span>
        <h4 class="question-text">{{ question.text }}</h4>
      </div>

      <div class="question-options">
        {% for option in question.options %}
        <button class="quiz-option" data-option-index="{{ loop.index0 }}">
          <span class="option-letter">{{ ['A', 'B', 'C', 'D'][loop.index0] }}</span>
          <span class="option-text">{{ option }}</span>
        </button>
        {% endfor %}
      </div>

      <div class="question-feedback" style="display: none;">
        <div class="feedback-content"></div>
        {% if question.explanation %}
        <p class="explanation">{{ question.explanation }}</p>
        {% endif %}
      </div>
    </div>
    {% endfor %}
  </div>

  <div class="quiz-results" style="display: none;">
    <div class="results-content">
      <h3 class="results-title">Quiz Complete! 🎉</h3>
      <div class="score-display">
        <span class="score-number"></span>
        <span class="score-label">out of {{ quizData.questions.length }}</span>
      </div>
      <p class="score-message"></p>
      <button class="quiz-restart">Try Again</button>
    </div>
  </div>

  <div class="quiz-progress">
    <div class="progress-bar">
      <div class="progress-fill" style="width: 0%"></div>
    </div>
    <p class="progress-text">0 of {{ quizData.questions.length }} answered</p>
  </div>
</div>

<style>
.interactive-quiz {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 16px;
  padding: 2rem;
  margin: 2rem 0;
  color: white;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
}

.quiz-header {
  text-align: center;
  margin-bottom: 2rem;
}

.quiz-title {
  font-size: 1.75rem;
  font-weight: 700;
  margin: 0 0 0.5rem 0;
}

.quiz-description {
  opacity: 0.9;
  font-size: 1rem;
}

.quiz-question {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 1.5rem;
  margin-bottom: 1rem;
  display: none;
}

.quiz-question.active {
  display: block;
  animation: slideIn 0.3s ease;
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.question-header {
  margin-bottom: 1.5rem;
}

.question-number {
  display: block;
  font-size: 0.875rem;
  opacity: 0.8;
  margin-bottom: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.question-text {
  font-size: 1.25rem;
  font-weight: 600;
  margin: 0;
}

.question-options {
  display: grid;
  gap: 0.75rem;
  margin-bottom: 1rem;
}

.quiz-option {
  background: rgba(255, 255, 255, 0.15);
  border: 2px solid transparent;
  border-radius: 8px;
  padding: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  cursor: pointer;
  transition: all 0.2s ease;
  color: white;
  font-size: 1rem;
  text-align: left;
  width: 100%;
}

.quiz-option:hover:not(:disabled) {
  background: rgba(255, 255, 255, 0.25);
  transform: translateX(4px);
}

.quiz-option:disabled {
  cursor: not-allowed;
  opacity: 0.6;
}

.quiz-option.correct {
  background: rgba(16, 185, 129, 0.3);
  border-color: #10b981;
}

.quiz-option.incorrect {
  background: rgba(239, 68, 68, 0.3);
  border-color: #ef4444;
}

.option-letter {
  background: rgba(255, 255, 255, 0.2);
  width: 32px;
  height: 32px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  flex-shrink: 0;
}

.option-text {
  flex: 1;
}

.question-feedback {
  margin-top: 1rem;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;
}

.feedback-content {
  font-weight: 600;
  margin-bottom: 0.5rem;
}

.explanation {
  opacity: 0.9;
  font-size: 0.9rem;
  margin: 0;
}

.quiz-results {
  text-align: center;
  padding: 2rem;
}

.results-title {
  font-size: 2rem;
  margin-bottom: 1.5rem;
}

.score-display {
  font-size: 3rem;
  font-weight: 700;
  margin-bottom: 1rem;
  display: flex;
  align-items: baseline;
  justify-content: center;
  gap: 0.5rem;
}

.score-label {
  font-size: 1.5rem;
  opacity: 0.8;
}

.score-message {
  font-size: 1.25rem;
  margin-bottom: 2rem;
}

.quiz-restart {
  background: white;
  color: #667eea;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.2s ease;
}

.quiz-restart:hover {
  transform: scale(1.05);
}

.quiz-progress {
  margin-top: 1.5rem;
}

.progress-bar {
  background: rgba(255, 255, 255, 0.2);
  height: 8px;
  border-radius: 4px;
  overflow: hidden;
  margin-bottom: 0.5rem;
}

.progress-fill {
  background: white;
  height: 100%;
  transition: width 0.3s ease;
}

.progress-text {
  text-align: center;
  font-size: 0.875rem;
  opacity: 0.8;
  margin: 0;
}

@media (max-width: 768px) {
  .interactive-quiz {
    padding: 1.5rem;
    margin: 1.5rem -1rem;
    border-radius: 0;
  }

  .quiz-title {
    font-size: 1.5rem;
  }

  .question-text {
    font-size: 1.1rem;
  }
}
</style>

<script>
document.addEventListener('DOMContentLoaded', function() {
  const quizzes = document.querySelectorAll('.interactive-quiz');
  
  quizzes.forEach(quiz => {
    let currentQuestion = 0;
    let score = 0;
    let answered = 0;
    
    const questions = quiz.querySelectorAll('.quiz-question');
    const resultsDiv = quiz.querySelector('.quiz-results');
    const progressFill = quiz.querySelector('.progress-fill');
    const progressText = quiz.querySelector('.progress-text');
    const restartBtn = quiz.querySelector('.quiz-restart');
    
    // Show first question
    if (questions.length > 0) {
      questions[0].classList.add('active');
    }
    
    // Handle option clicks
    questions.forEach((question, qIndex) => {
      const options = question.querySelectorAll('.quiz-option');
      const feedback = question.querySelector('.question-feedback');
      const feedbackContent = question.querySelector('.feedback-content');
      const correctAnswer = parseInt(question.dataset.correctAnswer);
      
      options.forEach((option, oIndex) => {
        option.addEventListener('click', function() {
          if (this.disabled) return;
          
          // Disable all options
          options.forEach(opt => opt.disabled = true);
          
          // Check answer
          const isCorrect = oIndex === correctAnswer;
          
          if (isCorrect) {
            this.classList.add('correct');
            feedbackContent.innerHTML = '✅ Correct!';
            score++;
          } else {
            this.classList.add('incorrect');
            options[correctAnswer].classList.add('correct');
            feedbackContent.innerHTML = '❌ Incorrect. The correct answer is highlighted.';
          }
          
          // Show feedback
          feedback.style.display = 'block';
          
          // Update progress
          answered++;
          updateProgress();
          
          // Track interaction
          if (typeof gtag !== 'undefined') {
            gtag('event', 'quiz_answer', {
              quiz_id: quiz.dataset.quizId,
              question_index: qIndex,
              is_correct: isCorrect
            });
          }
          
          // Move to next question after delay
          setTimeout(() => {
            question.classList.remove('active');
            currentQuestion++;
            
            if (currentQuestion < questions.length) {
              questions[currentQuestion].classList.add('active');
            } else {
              showResults();
            }
          }, 2000);
        });
      });
    });
    
    function updateProgress() {
      const percentage = (answered / questions.length) * 100;
      progressFill.style.width = percentage + '%';
      progressText.textContent = `${answered} of ${questions.length} answered`;
    }
    
    function showResults() {
      const percentage = (score / questions.length) * 100;
      let message = '';
      
      if (percentage === 100) {
        message = 'Perfect score! You\'re a master! 🏆';
      } else if (percentage >= 80) {
        message = 'Excellent work! You really know your stuff! 🌟';
      } else if (percentage >= 60) {
        message = 'Good job! You\'re on the right track! 👍';
      } else {
        message = 'Keep learning! Try again to improve your score! 📚';
      }
      
      quiz.querySelector('.score-number').textContent = score;
      quiz.querySelector('.score-message').textContent = message;
      resultsDiv.style.display = 'block';
      
      // Track completion
      if (typeof gtag !== 'undefined') {
        gtag('event', 'quiz_complete', {
          quiz_id: quiz.dataset.quizId,
          score: score,
          total: questions.length,
          percentage: percentage
        });
      }
    }
    
    // Restart quiz
    restartBtn.addEventListener('click', function() {
      currentQuestion = 0;
      score = 0;
      answered = 0;
      
      resultsDiv.style.display = 'none';
      updateProgress();
      
      questions.forEach(question => {
        question.classList.remove('active');
        const options = question.querySelectorAll('.quiz-option');
        options.forEach(opt => {
          opt.disabled = false;
          opt.classList.remove('correct', 'incorrect');
        });
        question.querySelector('.question-feedback').style.display = 'none';
      });
      
      questions[0].classList.add('active');
    });
  });
});
</script>
{% endmacro %}

{# Lead Magnet Gated Content Component #}
{# Displays downloadable resources with email capture #}

{% if leadMagnet %}
<aside class="lead-magnet-container" data-magnet-id="{{ leadMagnet.id }}">
  <div class="lead-magnet-card">
    <div class="lead-magnet-icon">
      {% if leadMagnet.type == 'pdf' %}
        📄
      {% elif leadMagnet.type == 'checklist' %}
        ✅
      {% elif leadMagnet.type == 'template' %}
        📋
      {% elif leadMagnet.type == 'guide' %}
        📚
      {% else %}
        📦
      {% endif %}
    </div>
    
    <div class="lead-magnet-content">
      <h3 class="lead-magnet-title">{{ leadMagnet.title }}</h3>
      <p class="lead-magnet-description">{{ leadMagnet.description }}</p>
      
      <div class="lead-magnet-benefits">
        <ul>
          {% for benefit in leadMagnet.benefits %}
          <li>{{ benefit }}</li>
          {% endfor %}
        </ul>
      </div>
      
      <form class="lead-magnet-form" data-magnet-form="{{ leadMagnet.id }}">
        <div class="form-group">
          <input 
            type="email" 
            name="email" 
            placeholder="Enter your email address" 
            required 
            class="lead-magnet-email-input"
            aria-label="Email address for download"
          >
        </div>
        <button type="submit" class="lead-magnet-submit-btn">
          <span class="btn-text">Download {{ leadMagnet.type | capitalize }}</span>
          <span class="btn-icon">⬇️</span>
        </button>
        <p class="lead-magnet-privacy">
          🔒 We respect your privacy. Unsubscribe at any time.
        </p>
      </form>
      
      <div class="lead-magnet-success" style="display: none;">
        <div class="success-icon">✅</div>
        <h4>Check your email!</h4>
        <p>We've sent the download link to your inbox.</p>
      </div>
    </div>
  </div>
</aside>

<script>
(function() {
  'use strict';
  
  const form = document.querySelector('[data-magnet-form="{{ leadMagnet.id }}"]');
  if (!form) return;
  
  form.addEventListener('submit', async (e) => {
    e.preventDefault();
    
    const email = form.querySelector('input[name="email"]').value;
    const magnetId = '{{ leadMagnet.id }}';
    const magnetTitle = '{{ leadMagnet.title }}';
    const submitBtn = form.querySelector('.lead-magnet-submit-btn');
    
    // Disable button during submission
    submitBtn.disabled = true;
    submitBtn.innerHTML = '<span class="btn-text">Processing...</span>';
    
    try {
      // Track download attempt
      if (typeof gtag !== 'undefined') {
        gtag('event', 'lead_magnet_download', {
          magnet_id: magnetId,
          magnet_title: magnetTitle,
          magnet_type: '{{ leadMagnet.type }}'
        });
      }
      
      // Send to Netlify Function for processing
      const response = await fetch('/.netlify/functions/lead-magnet', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          magnetId: magnetId,
          magnetTitle: magnetTitle,
          magnetType: '{{ leadMagnet.type }}',
          downloadUrl: '{{ leadMagnet.downloadUrl }}',
          pageUrl: window.location.href
        })
      });
      
      const data = await response.json();
      
      if (response.ok) {
        // Show success message
        form.style.display = 'none';
        document.querySelector('.lead-magnet-success').style.display = 'block';
        
        // Track successful download
        if (typeof gtag !== 'undefined') {
          gtag('event', 'conversion', {
            send_to: 'AW-CONVERSION_ID/CONVERSION_LABEL',
            event_category: 'Lead Magnet',
            event_label: magnetTitle
          });
        }
        
        // Trigger download if URL provided
        if (data.downloadUrl) {
          setTimeout(() => {
            window.open(data.downloadUrl, '_blank');
          }, 1000);
        }
      } else {
        throw new Error(data.error || 'Failed to process request');
      }
    } catch (error) {
      console.error('Lead magnet error:', error);
      alert('Sorry, something went wrong. Please try again.');
      submitBtn.disabled = false;
      submitBtn.innerHTML = '<span class="btn-text">Download {{ leadMagnet.type | capitalize }}</span><span class="btn-icon">⬇️</span>';
    }
  });
})();
</script>
{% endif %}

{# Related Posts Component #}
{# Displays 3 related posts based on tags and hub #}

{% set relatedPosts = [] %}
{% set currentUrl = page.url %}
{% set currentTags = tags or [] %}
{% set currentHub = hub %}

{# Score and collect all posts #}
{% for post in collections.blog %}
  {% if post.url != currentUrl %}
    {% set score = 0 %}
    
    {# Same hub: +3 points #}
    {% if post.data.hub == currentHub %}
      {% set score = score + 3 %}
    {% endif %}
    
    {# Shared tags: +2 points per tag #}
    {% if post.data.tags %}
      {% for tag in post.data.tags %}
        {% if tag in currentTags %}
          {% set score = score + 2 %}
        {% endif %}
      {% endfor %}
    {% endif %}
    
    {# Same author: +1 point #}
    {% if post.data.author == author %}
      {% set score = score + 1 %}
    {% endif %}
    
    {# Only include if score > 0 #}
    {% if score > 0 %}
      {% set relatedPosts = (relatedPosts.push({
        post: post,
        score: score
      }), relatedPosts) %}
    {% endif %}
  {% endif %}
{% endfor %}

{# Sort by score and take top 3 #}
{% if relatedPosts.length > 0 %}
<section class="related-posts">
  <h2 class="related-posts-title">Related Articles</h2>
  <div class="related-posts-grid">
    {% for item in relatedPosts | sort(attribute='score') | reverse | limit(3) %}
      {% set relatedPost = item.post %}
      <article class="related-post-card">
        {% if relatedPost.data.image %}
        <div class="related-post-image">
          <a href="{{ relatedPost.url }}">
            <img src="{{ relatedPost.data.image }}" alt="{{ relatedPost.data.imageAlt or relatedPost.data.title }}" loading="lazy">
          </a>
        </div>
        {% endif %}
        <div class="related-post-content">
          <h3 class="related-post-title">
            <a href="{{ relatedPost.url }}">{{ relatedPost.data.title }}</a>
          </h3>
          <p class="related-post-excerpt">{{ relatedPost.data.description or relatedPost.data.excerpt }}</p>
          <div class="related-post-meta">
            <span class="related-post-date">{{ relatedPost.date | postDate }}</span>
            {% if relatedPost.data.readingTime %}
            <span class="related-post-reading-time">{{ relatedPost.data.readingTime }} min read</span>
            {% endif %}
          </div>
        </div>
      </article>
    {% endfor %}
  </div>
</section>
{% endif %}

{% if series %}
{# Get all posts in this series #}
{% set seriesPosts = collections.all | filterBySeriesName(series) | sortBySeriesOrder %}
{% set currentIndex = seriesPosts | findIndexByUrl(page.url) %}
{% set totalParts = seriesPosts.length %}
{% set prevPost = seriesPosts[currentIndex - 1] if currentIndex > 0 else null %}
{% set nextPost = seriesPosts[currentIndex + 1] if currentIndex < totalParts - 1 else null %}

<div class="series-navigation">
  <div class="series-header">
    <div class="series-badge">
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20"></path>
        <path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z"></path>
      </svg>
      <span>Series</span>
    </div>
    <h3 class="series-title">{{ series }}</h3>
    <p class="series-progress">Part {{ seriesOrder | default(currentIndex + 1) }} of {{ totalParts }}</p>
  </div>

  {% if totalParts > 1 %}
  <div class="series-parts">
    <h4 class="series-parts-title">All Parts in This Series:</h4>
    <ol class="series-list">
      {% for post in seriesPosts %}
      <li class="series-item {{ 'current' if post.url == page.url else '' }}">
        {% if post.url == page.url %}
          <span class="series-item-number">{{ loop.index }}</span>
          <span class="series-item-title">{{ post.data.title }}</span>
          <span class="series-item-badge">You are here</span>
        {% else %}
          <a href="{{ post.url }}" class="series-item-link">
            <span class="series-item-number">{{ loop.index }}</span>
            <span class="series-item-title">{{ post.data.title }}</span>
            {% if loop.index < currentIndex + 1 %}
              <span class="series-item-status completed">✓</span>
            {% else %}
              <span class="series-item-status upcoming">→</span>
            {% endif %}
          </a>
        {% endif %}
      </li>
      {% endfor %}
    </ol>
  </div>

  <div class="series-navigation-buttons">
    {% if prevPost %}
    <a href="{{ prevPost.url }}" class="series-nav-button prev">
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M15 18l-6-6 6-6"></path>
      </svg>
      <div class="series-nav-content">
        <span class="series-nav-label">Previous</span>
        <span class="series-nav-title">{{ prevPost.data.title }}</span>
      </div>
    </a>
    {% else %}
    <div class="series-nav-button disabled">
      <span class="series-nav-label">No previous post</span>
    </div>
    {% endif %}

    {% if nextPost %}
    <a href="{{ nextPost.url }}" class="series-nav-button next">
      <div class="series-nav-content">
        <span class="series-nav-label">Next</span>
        <span class="series-nav-title">{{ nextPost.data.title }}</span>
      </div>
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M9 18l6-6-6-6"></path>
      </svg>
    </a>
    {% else %}
    <div class="series-nav-button disabled">
      <span class="series-nav-label">No next post</span>
    </div>
    {% endif %}
  </div>
  {% endif %}
</div>

