      }
    };

    const times = postingTimes[platform][audience];

    const timesHTML = `
      <h4>Best Times to Post on ${platform.charAt(0).toUpperCase() + platform.slice(1)}</h4>
      <p>For ${audience.replace('-', ' ')} audience in ${timezone} timezone:</p>
      <ul>
        ${times.map(time => `<li>â° ${time}</li>`).join('')}
      </ul>
      <p style="margin-top: 1rem; font-size: 0.9rem; opacity: 0.9;">
        ðŸ’¡ Tip: Test these times and track your analytics to find what works best for YOUR specific audience!
      </p>
    `;

    result.querySelector('.result-times').innerHTML = timesHTML;
    result.style.display = 'block';

    // Track calculation
    if (typeof gtag !== 'undefined') {
      gtag('event', 'calculator_use', {
        calculator_type: 'posting_time',
        platform: platform,
        audience: audience
      });
    }
  }

  function calculateReachEstimate(calc, result) {
    const followers = parseInt(calc.querySelector('[id^="followers-reach-"]').value) || 0;
    const engagementRate = parseFloat(calc.querySelector('[id^="engagement-"]').value) || 0;
    const platform = calc.querySelector('[id^="platform-reach-"]').value;

    // Platform reach multipliers (simplified estimates)
    const reachMultipliers = {
      tiktok: 3.5,      // TikTok has high viral potential
      instagram: 1.2,   // Instagram reach is limited
      youtube: 2.0,     // YouTube has good recommendation system
      twitter: 1.5      // Twitter has moderate reach
    };

    const multiplier = reachMultipliers[platform];
    const organicReach = Math.round(followers * (engagementRate / 100) * multiplier);
    const minReach = Math.round(organicReach * 0.7);
    const maxReach = Math.round(organicReach * 1.5);

    const reachHTML = `
      <h4>Estimated Reach</h4>
      <div class="result-value">${organicReach.toLocaleString()}</div>
      <p>Expected range: ${minReach.toLocaleString()} - ${maxReach.toLocaleString()} people</p>
      <p style="margin-top: 1rem; font-size: 0.9rem; opacity: 0.9;">
        ðŸ“Š This estimate is based on your follower count, engagement rate, and ${platform}'s typical reach patterns.
        Actual reach can vary significantly based on content quality, timing, and trends.
      </p>
    `;

    result.querySelector('.result-reach').innerHTML = reachHTML;
    result.style.display = 'block';

    // Track calculation
    if (typeof gtag !== 'undefined') {
      gtag('event', 'calculator_use', {
        calculator_type: 'reach_estimate',
        platform: platform,
        result: organicReach
      });
    }
  }
});
</script>
{% endmacro %}

{% macro quiz(quizData) %}
<div class="interactive-quiz" data-quiz-id="{{ quizData.id }}">
  <div class="quiz-header">
    <h3 class="quiz-title">{{ quizData.title }}</h3>
    {% if quizData.description %}
    <p class="quiz-description">{{ quizData.description }}</p>
    {% endif %}
  </div>

  <div class="quiz-questions">
    {% for question in quizData.questions %}
    <div class="quiz-question" data-question-index="{{ loop.index0 }}" data-correct-answer="{{ question.correctAnswer }}">
      <div class="question-header">
        <span class="question-number">Question {{ loop.index }} of {{ quizData.questions.length }}</span>
        <h4 class="question-text">{{ question.text }}</h4>
      </div>

      <div class="question-options">
        {% for option in question.options %}
        <button class="quiz-option" data-option-index="{{ loop.index0 }}">
          <span class="option-letter">{{ ['A', 'B', 'C', 'D'][loop.index0] }}</span>
          <span class="option-text">{{ option }}</span>
        </button>
        {% endfor %}
      </div>

      <div class="question-feedback" style="display: none;">
        <div class="feedback-content"></div>
        {% if question.explanation %}
        <p class="explanation">{{ question.explanation }}</p>
        {% endif %}
      </div>
    </div>
    {% endfor %}
  </div>

  <div class="quiz-results" style="display: none;">
    <div class="results-content">
      <h3 class="results-title">Quiz Complete! ðŸŽ‰</h3>
      <div class="score-display">
        <span class="score-number"></span>
        <span class="score-label">out of {{ quizData.questions.length }}</span>
      </div>
      <p class="score-message"></p>
      <button class="quiz-restart">Try Again</button>
    </div>
  </div>

  <div class="quiz-progress">
    <div class="progress-bar">
      <div class="progress-fill" style="width: 0%"></div>
    </div>
    <p class="progress-text">0 of {{ quizData.questions.length }} answered</p>
  </div>
</div>

<style>
.interactive-quiz {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 16px;
  padding: 2rem;
  margin: 2rem 0;
