    opacity: 1;
    transform: translateY(0);
  }
}

.question-header {
  margin-bottom: 1.5rem;
}

.question-number {
  display: block;
  font-size: 0.875rem;
  opacity: 0.8;
  margin-bottom: 0.5rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.question-text {
  font-size: 1.25rem;
  font-weight: 600;
  margin: 0;
}

.question-options {
  display: grid;
  gap: 0.75rem;
  margin-bottom: 1rem;
