.quiz-option {
  background: rgba(255, 255, 255, 0.15);
  border: 2px solid transparent;
  border-radius: 8px;
  padding: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  cursor: pointer;
  transition: all 0.2s ease;
  color: white;
  font-size: 1rem;
  text-align: left;
  width: 100%;
}

.quiz-option:hover:not(:disabled) {
  background: rgba(255, 255, 255, 0.25);
