{# Related Posts Component #}
{# Displays 3 related posts based on tags and hub #}

{% set relatedPosts = [] %}
{% set currentUrl = page.url %}
{% set currentTags = tags or [] %}
{% set currentHub = hub %}

{# Score and collect all posts #}
{% for post in collections.blog %}
  {% if post.url != currentUrl %}
    {% set score = 0 %}

    {# Same hub: +3 points #}
    {% if post.data.hub == currentHub %}
      {% set score = score + 3 %}
    {% endif %}

    {# Shared tags: +2 points per tag #}
    {% if post.data.tags %}
      {% for tag in post.data.tags %}
        {% if tag in currentTags %}
          {% set score = score + 2 %}
        {% endif %}
      {% endfor %}
    {% endif %}

    {# Same author: +1 point #}
    {% if post.data.author == author %}
      {% set score = score + 1 %}
    {% endif %}

    {# Only include if score > 0 #}
    {% if score > 0 %}
      {% set relatedPosts = (relatedPosts.push({
        post: post,
        score: score
      }), relatedPosts) %}
    {% endif %}
  {% endif %}
{% endfor %}

{# Sort by score and take top 3 #}
{% if relatedPosts.length > 0 %}
<section class="related-posts">
  <h2 class="related-posts-title">Related Articles</h2>
  <div class="related-posts-grid">
    {% for item in relatedPosts | sort(attribute='score') | reverse | limit(3) %}
      {% set relatedPost = item.post %}
      <article class="related-post-card">
        {% if relatedPost.data.image %}
        <div class="related-post-image">
          <a href="{{ relatedPost.url }}">
            <img src="{{ relatedPost.data.image }}" alt="{{ relatedPost.data.imageAlt or relatedPost.data.title }}" loading="lazy">
          </a>
        </div>
        {% endif %}
        <div class="related-post-content">
          <h3 class="related-post-title">
            <a href="{{ relatedPost.url }}">{{ relatedPost.data.title }}</a>
          </h3>
          <p class="related-post-excerpt">{{ relatedPost.data.description or relatedPost.data.excerpt }}</p>
          <div class="related-post-meta">
            <span class="related-post-date">{{ relatedPost.date | postDate }}</span>
            {% if relatedPost.data.readingTime %}
            <span class="related-post-reading-time">{{ relatedPost.data.readingTime }} min read</span>
            {% endif %}
          </div>
        </div>
      </article>
    {% endfor %}
  </div>
</section>
{% endif %}

{% if series %}
{# Get all posts in this series #}
{% set seriesPosts = collections.all | filterBySeriesName(series) | sortBySeriesOrder %}
{% set currentIndex = seriesPosts | findIndexByUrl(page.url) %}
{% set totalParts = seriesPosts.length %}
{% set prevPost = seriesPosts[currentIndex - 1] if currentIndex > 0 else null %}
{% set nextPost = seriesPosts[currentIndex + 1] if currentIndex < totalParts - 1 else null %}

<div class="series-navigation">
  <div class="series-header">
    <div class="series-badge">
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20"></path>
        <path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z"></path>
      </svg>
      <span>Series</span>
    </div>
    <h3 class="series-title">{{ series }}</h3>
    <p class="series-progress">Part {{ seriesOrder | default(currentIndex + 1) }} of {{ totalParts }}</p>
  </div>

  {% if totalParts > 1 %}
  <div class="series-parts">
    <h4 class="series-parts-title">All Parts in This Series:</h4>
    <ol class="series-list">
      {% for post in seriesPosts %}
      <li class="series-item {{ 'current' if post.url == page.url else '' }}">
        {% if post.url == page.url %}
          <span class="series-item-number">{{ loop.index }}</span>
          <span class="series-item-title">{{ post.data.title }}</span>
          <span class="series-item-badge">You are here</span>
        {% else %}
          <a href="{{ post.url }}" class="series-item-link">
            <span class="series-item-number">{{ loop.index }}</span>
            <span class="series-item-title">{{ post.data.title }}</span>
            {% if loop.index < currentIndex + 1 %}
              <span class="series-item-status completed">âœ“</span>
            {% else %}
              <span class="series-item-status upcoming">â†’</span>
            {% endif %}
          </a>
        {% endif %}
      </li>
      {% endfor %}
    </ol>
  </div>

  <div class="series-navigation-buttons">
    {% if prevPost %}
    <a href="{{ prevPost.url }}" class="series-nav-button prev">
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M15 18l-6-6 6-6"></path>
      </svg>
      <div class="series-nav-content">
        <span class="series-nav-label">Previous</span>
        <span class="series-nav-title">{{ prevPost.data.title }}</span>
