{# Interactive Table of Contents Component #}
{# Auto-generates TOC from H2/H3 headings with sticky sidebar and scroll highlighting #}

<aside class="table-of-contents" id="toc-sidebar">
  <div class="toc-header">
    <h2>Table of Contents</h2>
    <button class="toc-toggle" aria-label="Toggle table of contents">
      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <polyline points="6 9 12 15 18 9"></polyline>
      </svg>
    </button>
  </div>
  <nav class="toc-nav">
    <ul class="toc-list" id="toc-list">
      <!-- Generated dynamically by JavaScript -->
    </ul>
  </nav>
</aside>

<script>
(function() {
  'use strict';

  // Only run on article pages
  const articleBody = document.querySelector('.article_bodydiv');
  if (!articleBody) return;

  const tocList = document.getElementById('toc-list');
  const tocSidebar = document.getElementById('toc-sidebar');
  if (!tocList || !tocSidebar) return;

  // Get all H2 and H3 headings from article
  const headings = articleBody.querySelectorAll('h3');

  if (headings.length === 0) {
    // Hide TOC if no headings found
    tocSidebar.style.display = 'none';
    return;
  }

  // Generate TOC items
  headings.forEach((heading, index) => {
    // Create unique ID for heading if it doesn't have one
    if (!heading.id) {
      heading.id = 'heading-' + index;
    }

    const li = document.createElement('li');
    li.className = heading.tagName.toLowerCase() === 'h3' ? 'toc-item toc-item-h3' : 'toc-item toc-item-h2';

    const a = document.createElement('a');
    a.href = '#' + heading.id;
    a.textContent = heading.textContent;
    a.className = 'toc-link';

    // Smooth scroll to section
    a.addEventListener('click', (e) => {
      e.preventDefault();
      heading.scrollIntoView({ behavior: 'smooth', block: 'start' });

      // Update URL hash without jumping
      history.pushState(null, null, '#' + heading.id);

      // Update active state
      document.querySelectorAll('.toc-link').forEach(link => link.classList.remove('active'));
      a.classList.add('active');
    });

    li.appendChild(a);
    tocList.appendChild(li);
  });

  // Highlight current section on scroll
  let ticking = false;

  function updateActiveSection() {
    const scrollPosition = window.scrollY + 100; // Offset for header

    let currentHeading = null;
    headings.forEach(heading => {
      if (heading.offsetTop <= scrollPosition) {
        currentHeading = heading;
      }
    });

    if (currentHeading) {
      document.querySelectorAll('.toc-link').forEach(link => {
        link.classList.remove('active');
        if (link.getAttribute('href') === '#' + currentHeading.id) {
          link.classList.add('active');
        }
      });
    }
  }

  window.addEventListener('scroll', () => {
    if (!ticking) {
      window.requestAnimationFrame(() => {
        updateActiveSection();
        ticking = false;
      });
      ticking = true;
    }
  });

  // Initial update
  updateActiveSection();

  // Mobile toggle functionality
  const tocToggle = document.querySelector('.toc-toggle');
  const tocNav = document.querySelector('.toc-nav');

  if (tocToggle && tocNav) {
    tocToggle.addEventListener('click', () => {
      tocNav.classList.toggle('toc-nav-open');
      tocToggle.classList.toggle('toc-toggle-open');
    });
  }
})();
</script>

<style>
.table-of-contents {
  position: sticky;
  top: 100px;
  max-height: calc(100vh - 120px);
  overflow-y: auto;
  background: white;
  border-radius: 12px;
  padding: 1.5rem;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid #e1e5e9;
}

.toc-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
