  transition: transform 0.3s ease;
}

.toc-toggle svg {
  display: block;
}

.toc-toggle-open {
  transform: rotate(180deg);
}

.toc-nav {
  max-height: none;
  overflow: visible;
}

.toc-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.toc-item {
  margin: 0;
}

.toc-item-h2 {
  margin-bottom: 0.5rem;
}

.toc-item-h3 {
  margin-left: 1rem;
  margin-bottom: 0.4rem;
}

.toc-link {
  display: block;
  padding: 0.5rem 0.75rem;
  color: #666;
  text-decoration: none;
  border-left: 3px solid transparent;
  transition: all 0.2s ease;
  font-size: 0.9rem;
  line-height: 1.4;
}

.toc-link:hover {
  color: #33bdef;
  background: #f8f9fa;
  border-left-color: #33bdef;
}

.toc-link.active {
  color: #33bdef;
  background: #e8f7fc;
  border-left-color: #33bdef;
  font-weight: 600;
}

/* Dark mode support */
body.dark-mode .table-of-contents {
  background: #1e1e1e;
  border-color: #333;
}

body.dark-mode .toc-header h2 {
  color: #e0e0e0;
}

body.dark-mode .toc-link {
  color: #b0b0b0;
}

