<script>
// Copy the same search script from English version with French translations
const searchInput = document.getElementById('searchInput');
const autocompleteResults = document.getElementById('autocompleteResults');
let blogPosts = [
  {% for post in collections.blog %}
    {
      title: "{{ post.data.title }}",
      url: "{{ post.url }}",
      description: "{{ post.data.description }}",
      image: "{{ post.data.image }}",
      hub: "{{ post.data.hub }}",
      tags: {{ post.data.tags | dump | safe }},
      readingTime: "{{ post.data.readingTime }}",
      date: "{{ post.date | postDate }}"
    }{% if not loop.last %},{% endif %}
  {% endfor %}
];

function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

function fuzzyMatch(text, query) {
  if (!text || !query) return false;
  text = text.toLowerCase();
  query = query.toLowerCase();

  if (text.includes(query)) return 100;

  let queryIndex = 0;
  let score = 0;

  for (let i = 0; i < text.length && queryIndex < query.length; i++) {
    if (text[i] === query[queryIndex]) {
      score += 10;
      queryIndex++;
    }
  }

  return queryIndex === query.length ? score : 0;
}

function showAutocomplete(searchTerm) {
  if (!searchTerm) {
    autocompleteResults.innerHTML = '';
    autocompleteResults.style.display = 'none';
    return;
  }

  const scoredMatches = blogPosts.map(post => {
    const titleScore = fuzzyMatch(post.title, searchTerm);
    const descScore = fuzzyMatch(post.description, searchTerm) * 0.5;
    const tagScore = post.tags ? post.tags.reduce((acc, tag) => 
      acc + fuzzyMatch(tag, searchTerm) * 0.7, 0) : 0;

    return {
      post,
      score: titleScore + descScore + tagScore
    };
  }).filter(item => item.score > 0)
    .sort((a, b) => b.score - a.score);

  if (scoredMatches.length > 0) {
    const groupedResults = {};
    scoredMatches.slice(0, 10).forEach(({ post }) => {
      const hub = post.hub || 'Autres';
      if (!groupedResults[hub]) {
        groupedResults[hub] = [];
      }
      groupedResults[hub].push(post);
    });

    let html = '<div class="search-results-enhanced">';

    Object.keys(groupedResults).forEach(hub => {
      if (Object.keys(groupedResults).length > 1) {
        html += `<h3 class="search-results-group-title">${hub}</h3>`;
      }

      groupedResults[hub].forEach(post => {
        html += `
          <div class="search-result-item" onclick="window.location.href='${post.url}'">
            ${post.image ? `
              <div class="search-result-thumbnail">
                <img src="${post.image}" alt="${post.title}" loading="lazy">
              </div>
            ` : ''}
            <div class="search-result-content">
              <h4 class="search-result-title">${post.title}</h4>
              <div class="search-result-meta">
                <span>ðŸ“… ${post.date}</span>
                ${post.readingTime ? `<span>ðŸ“– ${post.readingTime} min lecture</span>` : ''}
              </div>
              <p class="search-result-description">${post.description}</p>
              ${post.tags && post.tags.length > 0 ? `
                <div class="search-result-tags">
                  ${post.tags.slice(0, 3).map(tag => 
                    `<span class="search-result-tag">${tag}</span>`
                  ).join('')}
                </div>
              ` : ''}
            </div>
          </div>
        `;
      });
    });

    html += '</div>';
    autocompleteResults.innerHTML = html;
    autocompleteResults.style.display = 'block';
  } else {
    autocompleteResults.innerHTML = `
      <div class="search-no-results">
        <div class="search-no-results-icon">ðŸ”</div>
        <div class="search-no-results-text">Aucun article trouvÃ©</div>
        <div class="search-no-results-suggestion">Essayez d'autres mots-clÃ©s</div>
      </div>
    `;
    autocompleteResults.style.display = 'block';
  }
}

const debouncedShowAutocomplete = debounce(showAutocomplete, 300);

searchInput.addEventListener('input', (e) => {
  debouncedShowAutocomplete(e.target.value);
});

document.addEventListener('click', (e) => {
  if (!searchInput.contains(e.target) && !autocompleteResults.contains(e.target)) {
    autocompleteResults.style.display = 'none';
  }
});

searchInput.addEventListener('focus', (e) => {
  if (e.target.value) {
    debouncedShowAutocomplete(e.target.value);
  }
});
</script>

