<script>
document.addEventListener('DOMContentLoaded', function() {
  const track = document.querySelector('.tools-carousel-track');
  const originalCards = document.querySelectorAll('.tool-card');

  if (!track || originalCards.length === 0) return;

  // Clone first and last cards for infinite loop
  const firstClone = originalCards[0].cloneNode(true);
  const lastClone = originalCards[originalCards.length - 1].cloneNode(true);

  // Add clones to DOM
  track.appendChild(firstClone);
  track.insertBefore(lastClone, originalCards[0]);

  const allCards = document.querySelectorAll('.tool-card');
  let currentIndex = 1; // Start at the first real card
  let isTransitioning = false;
  let cardWidth = originalCards[0].offsetWidth + 32; // card width + gap

  function updateCarousel(instant = false) {
    const offset = -currentIndex * cardWidth;
    track.style.transition = instant ? 'none' : 'transform 0.5s cubic-bezier(0.4, 0, 0.2, 1)';
    track.style.transform = `translateX(${offset}px)`;

    // Blur logic
    allCards.forEach((card, index) => {
      const dist = Math.abs(index - currentIndex);
      card.style.transition = 'all 0.5s ease';

      if (dist === 0) {
        card.style.opacity = '1';
        card.style.filter = 'blur(0px)';
        card.style.transform = 'scale(1)';
      } else if (dist === 1) {
        card.style.opacity = '0.6';
        card.style.filter = 'blur(1px)';
        card.style.transform = 'scale(0.95)';
      } else {
        card.style.opacity = '0.3';
        card.style.filter = 'blur(3px)';
        card.style.transform = 'scale(0.9)';
      }
    });
  }

  function nextSlide() {
    if (isTransitioning) return;
    isTransitioning = true;
    currentIndex++;
    updateCarousel();
  }

  // Handle transition end for infinite loop reset
  track.addEventListener('transitionend', () => {
    isTransitioning = false;

    // If we reached the clone of the first card (at the end)
    if (currentIndex === allCards.length - 1) {
      currentIndex = 1; // Jump back to the real first card
      updateCarousel(true);
    }

    // If we reached the clone of the last card (at the start)
    if (currentIndex === 0) {
      currentIndex = allCards.length - 2; // Jump to real last card
      updateCarousel(true);
    }
  });

  // Auto-cycle
  setInterval(nextSlide, 3000);

  // Handle resize
  window.addEventListener('resize', () => {
    cardWidth = originalCards[0].offsetWidth + 32;
    currentIndex = 1;
    updateCarousel(true);
  });

  // Initial setup
  setTimeout(() => {
    cardWidth = originalCards[0].offsetWidth + 32;
    updateCarousel(true);
  }, 100);
});
</script>

